BlogDetailOutput newDetailOutputFromJson(String str) => BlogDetailOutput.fromJson(json.decode(str));

String newDetailOutputToJson(BlogDetailOutput data) => json.encode(data.toJson());

class BlogDetailOutput {
  BlogDetailOutput({
    this.statusCode,
    this.status,
    this.message,
    this.data,
    this.errors,
  });

  BlogDetailOutput.fromJson(dynamic json) {
    statusCode = json['status_code'];
    status = json['status'];
    message = json['message'];
    data = json['data'] != null ? Data.fromJson(json['data']) : null;
    errors = json['errors'] != null ? json['errors'].cast<String>() : [];
  }

  num? statusCode;
  String? status;
  String? message;
  Data? data;
  List<String>? errors;

  BlogDetailOutput copyWith({
    num? statusCode,
    String? status,
    String? message,
    Data? data,
    List<String>? errors,
  }) =>
      BlogDetailOutput(
        statusCode: statusCode ?? this.statusCode,
        status: status ?? this.status,
        message: message ?? this.message,
        data: data ?? this.data,
        errors: errors ?? this.errors,
      );

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status_code'] = statusCode;
    map['status'] = status;
    map['message'] = message;
    if (data != null) {
      map['data'] = data?.toJson();
    }
    map['errors'] = errors;
    return map;
  }
}

