class Data {
  Data({
    this.id,
    this.newsCategoryId,
    this.name,
    this.slug,
    this.avatar,
    this.description,
    this.content,
    this.publishTime,

  });

  Data.fromJson(dynamic json) {
    id = json['id'] ?? 0;
    name = json['name'];
    slug = json['slug'];
    avatar = json['avatar'];
    description = json['description'];
    content = json['content'];
    publishTime = json['publish_time'];
    categoryName = json['category_name'];
  }

  num? id;
  num? newsCategoryId;
  String? name;
  String? slug;
  String? avatar;
  String? description;
  String? content;
  String? publishTime;
  String? categoryName;

  Data copyWith({
    num? id,
    num? newsCategoryId,
    String? name,
    String? slug,
    String? avatar,
    String? description,
    String? content,
    String? createdAt,
  }) =>
      Data(
        id: id ?? this.id,
        newsCategoryId: newsCategoryId ?? this.newsCategoryId,
        name: name ?? this.name,
        slug: slug ?? this.slug,
        avatar: avatar ?? this.avatar,
        description: description ?? this.description,
        content: content ?? this.content,
        publishTime: createdAt ?? this.publishTime,
      );

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = id;
    map['name'] = name;
    map['slug'] = slug;
    map['avatar'] = avatar;
    map['description'] = description;
    map['content'] = content;
    map['publish_time'] = publishTime;
    return map;
  }
